import React, { useState, useEffect } from 'react';
import {
    Box,
    TextField,
    Button,
    Typography,
    Paper,
    List,
    ListItem,
    CircularProgress,
    Table,
    TableBody,
    TableCell,
    TableContainer,
    TableHead,
    TableRow
} from '@mui/material';
import axios from '../utils/axiosConfig';
import { toast } from 'react-toastify';

const Chat = () => {
    const [messages, setMessages] = useState([]);
    const [input, setInput] = useState('');
    const [isLoading, setIsLoading] = useState(false);

    // Fetch welcome message and dataset preview on component mount
    useEffect(() => {
        const fetchWelcomeMessage = async () => {
            try {
                const response = await axios.get('/chat', {
                    headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
                });
                const { message, preview } = response.data;
                console

                setMessages([
                    { sender: 'system', text: message },
                    { sender: 'system', text: 'Dataset Preview:', preview }
                ]);
            } catch (error) {
                console.error('Error fetching chat:', error);
                toast.error('Failed to fetch welcome message');
            }
        };
        fetchWelcomeMessage();
    }, []);

    // Handle sending user input
    const handleSendMessage = async () => {
        if (!input.trim()) {
            toast.error('Please enter a valid command.');
            return;
        }

        setIsLoading(true);

        try {
            const response = await axios.post(
                '/transform',
                { command: input },
                { headers: { Authorization: `Bearer ${localStorage.getItem('token')}` } }
            );

            setMessages((prev) => [
                ...prev,
                { sender: 'user', text: input },
                { sender: 'ai', text: response.data.message },
                { sender: 'ai', text: 'Dataset Preview:', preview: response.data.preview }
            ]);
            setInput('');
        } catch (error) {
            console.error('Error in transformation:', error);
            toast.error(error.response?.data?.message || 'Failed to apply transformation');
        } finally {
            setIsLoading(false);
        }
    };

    // Render dataset preview as a table
    const renderPreview = (preview) => {
        if (!preview) return 'No dataset available';

        const columns = Object.keys(preview);
        const rows = Object.values(preview);

        return (
            <TableContainer component={Paper}>
                <Table size="small">
                    <TableHead>
                        <TableRow>
                            {columns.map((col, index) => (
                                <TableCell key={index}>{col}</TableCell>
                            ))}
                        </TableRow>
                    </TableHead>
                    <TableBody>
                        {rows[0].map((_, rowIndex) => (
                            <TableRow key={rowIndex}>
                                {columns.map((col, colIndex) => (
                                    <TableCell key={colIndex}>{preview[col][rowIndex]}</TableCell>
                                ))}
                            </TableRow>
                        ))}
                    </TableBody>
                </Table>
            </TableContainer>
        );
    };

    return (
        <Box
            sx={{
                height: '100vh',
                display: 'flex',
                flexDirection: 'column',
                justifyContent: 'center',
                alignItems: 'center',
                backgroundColor: '#3E2723',
            }}
        >
            <Paper
                elevation={6}
                sx={{
                    width: '100%',
                    maxWidth: 800,
                    height: '80%',
                    display: 'flex',
                    flexDirection: 'column',
                    borderRadius: 2,
                    backgroundColor: '#D84315',
                    color: '#fff',
                }}
            >
                <Typography
                    variant="h5"
                    textAlign="center"
                    sx={{
                        padding: 2,
                        borderBottom: '1px solid #E64A19',
                        backgroundColor: '#BF360C',
                    }}
                >
                    Chat with Intelligent Service
                </Typography>
                <Box sx={{ flex: 1, overflowY: 'auto', padding: 2 }}>
                    <List>
                        {messages.map((message, index) => (
                            <ListItem key={index} sx={{ justifyContent: message.sender === 'user' ? 'flex-end' : 'flex-start' }}>
                                <Paper
                                    elevation={3}
                                    sx={{
                                        padding: 1.5,
                                        maxWidth: '70%',
                                        borderRadius: '15px',
                                        backgroundColor: message.sender === 'user' ? '#FF7043' : '#F57C00',
                                        color: message.sender === 'user' ? '#fff' : '#000',
                                    }}
                                >
                                    {message.text === 'Dataset Preview:' && message.preview ? (
                                        renderPreview(message.preview)
                                    ) : (
                                        message.text
                                    )}
                                </Paper>
                            </ListItem>
                        ))}
                    </List>
                </Box>
                <Box
                    sx={{
                        display: 'flex',
                        padding: 2,
                        backgroundColor: '#BF360C',
                        borderTop: '1px solid #E64A19',
                        alignItems: 'center',
                    }}
                >
                    <TextField
                        placeholder="Type a command... (e.g., 'Remove rows where column A > 5')"
                        fullWidth
                        value={input}
                        onChange={(e) => setInput(e.target.value)}
                        sx={{
                            backgroundColor: '#F57C00',
                            borderRadius: 1,
                            input: { color: '#fff' },
                        }}
                        disabled={isLoading}
                    />
                    <Button
                        variant="contained"
                        color="secondary"
                        onClick={handleSendMessage}
                        disabled={isLoading}
                        sx={{
                            marginLeft: 2,
                            textTransform: 'none',
                            backgroundColor: '#D84315',
                            '&:hover': { backgroundColor: '#BF360C' },
                        }}
                    >
                        {isLoading ? <CircularProgress size={24} color="inherit" /> : 'Send'}
                    </Button>
                </Box>
            </Paper>
        </Box>
    );
};

export default Chat;
