import React, { useState } from 'react';
import { Container, Box, TextField, Paper, MenuItem, Select, FormControl, InputLabel, Button } from '@mui/material';
import axios from '../utils/axiosConfig';
import { useNavigate } from 'react-router-dom';
import { toast } from 'react-toastify';
import Navbar from './Navbar';
import { Typography } from '@mui/material';

const Home = () => {
    const [file, setFile] = useState(null);
    const [fileType, setFileType] = useState('csv'); // Default file type
    const navigate = useNavigate();

    const handleFileUpload = async () => {
        if (!file) {
            toast.warning('Please select a dataset to upload.');
            return;
        }

        if (!fileType) {
            toast.warning('Please select the file type.');
            return;
        }

        const formData = new FormData();
        formData.append('file', file);
        formData.append('file_type', fileType); // Send file type to backend

        try {
            const response = await axios.post('/home', formData, {
                headers: { 'Content-Type': 'multipart/form-data' },
            });
            toast.success(response.data.message || 'File uploaded successfully!');
            navigate('/chat'); // Redirect to chat page
        } catch (error) {
            console.log(error);
            toast.error(error.response?.data?.message || 'Upload failed');
        }
    };

    return (
        <>
            <Navbar />
            <Container
                sx={{
                    height: 'calc(100vh - 64px)', // Subtract navbar height
                    display: 'flex',
                    justifyContent: 'center',
                    alignItems: 'center',
                    backgroundColor: '#ffebc5', // Light yellow background
                }}
            >
                <Paper
                    elevation={6}
                    sx={{
                        padding: 4,
                        borderRadius: 2,
                        width: '100%',
                        maxWidth: 500,
                        textAlign: 'center',
                        backgroundColor: '#fff3e0', // Light orange for the paper
                        boxShadow: 3,
                    }}
                >
                    <Typography variant="h5" gutterBottom sx={{ color: '#d32f2f', fontWeight: '600' }}>
                        Upload Your Dataset
                    </Typography>
                    <Box sx={{ mt: 3, mb: 2 }}>
                        <TextField
                            type="file"
                            fullWidth
                            inputProps={{
                                onChange: (e) => setFile(e.target.files[0]),
                            }}
                            sx={{
                                backgroundColor: '#fff',
                                borderRadius: 1,
                                '& input': {
                                    color: '#000', // Adjust text color for file input
                                },
                            }}
                        />
                    </Box>
                    <FormControl fullWidth sx={{ mb: 2, backgroundColor: '#fff', borderRadius: 1 }}>
                        <InputLabel id="file-type-label" sx={{ color: '#d32f2f' }}>File Type</InputLabel>
                        <Select
                            labelId="file-type-label"
                            value={fileType}
                            onChange={(e) => setFileType(e.target.value)}
                            fullWidth
                            sx={{
                                color: '#000',
                                '& .MuiOutlinedInput-notchedOutline': {
                                    borderColor: '#d32f2f',
                                },
                                '&:hover .MuiOutlinedInput-notchedOutline': {
                                    borderColor: '#c2185b',
                                },
                            }}
                        >
                            <MenuItem value="csv">CSV</MenuItem>
                            <MenuItem value="tsv">TSV</MenuItem>
                        </Select>
                    </FormControl>
                    <Button
                        variant="contained"
                        color="error" // Use red color for buttons
                        fullWidth
                        size="large"
                        onClick={handleFileUpload}
                        sx={{
                            textTransform: 'none',
                            fontWeight: 'bold',
                            borderRadius: 2,
                            boxShadow: 3,
                            '&:hover': {
                                backgroundColor: '#b71c1c', // Darker red for hover
                                boxShadow: 5,
                            },
                        }}
                    >
                        Upload
                    </Button>
                </Paper>
            </Container>
        </>
    );
};

export default Home;
