import React, { useState, useEffect } from 'react';
import {
    Box,
    TextField,
    Button,
    Typography,
    Paper,
    List,
    ListItem,
    ListItemText,
    CircularProgress
} from '@mui/material';
import axios from '../utils/axiosConfig';
import { toast } from 'react-toastify';

const Chat = () => {
    const [messages, setMessages] = useState([]);
    const [input, setInput] = useState('');
    const [isLoading, setIsLoading] = useState(false);

    useEffect(() => {
        const fetchWelcomeMessage = async () => {
            try {
                const response = await axios.get('/chat', {
                    headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
                });
                const { message, preview } = response.data;
                console.log('message', message);
                cos
                setMessages([
                    { sender: 'system', text: message },
                    { sender: 'system', text: `Dataset Preview:\n${JSON.stringify(preview, null, 2)}` }
                ]);
            } catch (error) {
                toast.error('Failed to fetch welcome message');
            }
        };
        fetchWelcomeMessage();
    }, []);

    const handleSendMessage = async () => {
        if (!input.trim()) {
            toast.error('Please enter a valid command.');
            return;
        }

        setIsLoading(true);

        try {
            const response = await axios.post(
                '/transform',
                { command: input },
                { headers: { Authorization: `Bearer ${localStorage.getItem('token')}` } }
            );

            setMessages((prev) => [
                ...prev,
                { sender: 'user', text: input },
                { sender: 'ai', text: response.data.message },
                { sender: 'ai', text: `Preview:\n${JSON.stringify(response.data.preview, null, 2)}` }
            ]);
            setInput('');
        } catch (error) {
            toast.error(error.response?.data?.message || 'Failed to apply transformation');
        } finally {
            setIsLoading(false);
        }
    };

    return (
        <Box
            sx={{
                height: '100vh',
                display: 'flex',
                flexDirection: 'column',
                justifyContent: 'center',
                alignItems: 'center',
                backgroundColor: '#3E2723',
            }}
        >
            <Paper
                elevation={6}
                sx={{
                    width: '100%',
                    maxWidth: 800,
                    height: '80%',
                    display: 'flex',
                    flexDirection: 'column',
                    borderRadius: 2,
                    backgroundColor: '#D84315',
                    color: '#fff',
                }}
            >
                <Typography
                    variant="h5"
                    textAlign="center"
                    sx={{
                        padding: 2,
                        borderBottom: '1px solid #E64A19',
                        backgroundColor: '#BF360C',
                    }}
                >
                    Chat with Intelligent Service
                </Typography>
                <Box sx={{ flex: 1, overflowY: 'auto', padding: 2 }}>
                    <List>
                        {messages.map((message, index) => (
                            <ListItem key={index} sx={{ justifyContent: message.sender === 'user' ? 'flex-end' : 'flex-start' }}>
                                <Paper
                                    elevation={3}
                                    sx={{
                                        padding: 1.5,
                                        maxWidth: '70%',
                                        borderRadius: '15px',
                                        backgroundColor: message.sender === 'user' ? '#FF7043' : '#F57C00',
                                        color: message.sender === 'user' ? '#fff' : '#000',
                                    }}
                                >
                                    <ListItemText primary={message.text} />
                                </Paper>
                            </ListItem>
                        ))}
                    </List>
                </Box>
                <Box
                    sx={{
                        display: 'flex',
                        padding: 2,
                        backgroundColor: '#BF360C',
                        borderTop: '1px solid #E64A19',
                        alignItems: 'center',
                    }}
                >
                    <TextField
                        placeholder="Type a command... (e.g., 'Remove rows where column A > 5')"
                        fullWidth
                        value={input}
                        onChange={(e) => setInput(e.target.value)}
                        sx={{
                            backgroundColor: '#F57C00',
                            borderRadius: 1,
                            input: { color: '#fff' },
                        }}
                        disabled={isLoading}
                    />
                    <Button
                        variant="contained"
                        color="secondary"
                        onClick={handleSendMessage}
                        disabled={isLoading}
                        sx={{
                            marginLeft: 2,
                            textTransform: 'none',
                            backgroundColor: '#D84315',
                            '&:hover': { backgroundColor: '#BF360C' },
                        }}
                    >
                        {isLoading ? <CircularProgress size={24} color="inherit" /> : 'Send'}
                    </Button>
                </Box>
            </Paper>
        </Box>
    );
};

export default Chat;
